mapper.select:
select instance.id, instance.process_definition_code, instance.command_type, instance.name, instance.state, instance.start_time as start_date_time, instance.end_time as end_date_time, date_format(instance.start_time,'%Y-%m-%d %H:%i:%s') as start_time, date_format(instance.end_time,'%Y-%m-%d %H:%i:%s') as end_time, instance.run_times, case WHEN instance.command_type in (9) and instance.state in(7) THEN '停止' WHEN instance.state in (6) THEN '失败' WHEN instance.state in (0,1,8,10,11) THEN '执行中' WHEN instance.state in(2,3) THEN '暂停' WHEN instance.state in(4,5) THEN '停止' WHEN instance.state in(7) THEN '完成' WHEN instance.state in(9) THEN '未知' ELSE '未知' END as state_name from t_ds_process_instance instance right join bz_zr_process_out_put_export export on export.process_id = instance.process_definition_code where instance.is_sub_process=0 and instance.process_definition_code = #{processDefinitionId}
and DATE_FORMAT(instance.start_time,'%Y-%m-%d') like concat(#{startTimeFilterVal}, '%')
order by instance.start_time desc

select process_id, code, datasource_json, datasource_name
from bz_zr_process_out_put_export
where process_id = #{processDefinitionId} and datasource_name = #{dataSourceName}
