mapper.select:
select pro.id, pro.process_definition_code, pro.command_type, pro.name, pro.state, pro.start_date_time, pro.end_date_time, pro.start_time, pro.end_time, pro.run_times, pro.user_name, pro.project_name, pro.state_name, pro.schedule_date_time from (select instance.id, instance.process_definition_code, instance.command_type, instance.executor_id, instance.name, instance.state, instance.start_time as start_date_time, instance.end_time as end_date_time, date_format(instance.start_time,'%Y-%m-%d %H:%i:%s') as start_time, date_format(instance.end_time,'%Y-%m-%d %H:%i:%s') as end_time, instance.run_times, u.user_name, p.name as project_name, case WHEN instance.command_type in (9) and instance.state in(7) THEN '停止' WHEN instance.state in (6) THEN '失败' WHEN instance.state in (0,1,8,10,11) THEN '执行中' WHEN instance.state in(2,3) THEN '暂停' WHEN instance.state in(4,5) THEN '停止' WHEN instance.state in(7) THEN '完成' WHEN instance.state in(9) THEN '未知' ELSE '未知' END as state_name, date_format(instance.schedule_time,'%Y-%m-%d %H:%i:%s') as schedule_date_time from t_ds_process_instance instance join t_ds_process_definition define ON instance.process_definition_code = define.code left join t_ds_user u on instance.executor_id = u.id join t_ds_project p on define.project_code = p.id where instance.is_sub_process=0 and define.project_code = #{projectCode} and LOCATE("COMPLEMENT_DATA",instance.history_cmd)=0
and instance.process_definition_code = #{processDefinitionCode}
and instance.start_time > #{startTime} and instance.start_time <= #{endTime}
and instance.executor_id = #{executorId}
order by instance.start_time desc ) pro where 1=1 and pro.state_name=#{stateVal}

select pro.id, pro.process_definition_code, pro.command_type, pro.name, pro.state, pro.start_date_time, pro.end_date_time, pro.start_time, pro.end_time, pro.run_times, pro.user_name, pro.project_name, pro.state_name, pro.schedule_date_time from (select instance.id, instance.process_definition_code, instance.command_type, instance.executor_id, instance.name, instance.state, instance.start_time as start_date_time, instance.end_time as end_date_time, date_format(instance.start_time,'%Y-%m-%d %H:%i:%s') as start_time, date_format(instance.end_time,'%Y-%m-%d %H:%i:%s') as end_time, instance.run_times, u.user_name, p.name as project_name, case WHEN instance.command_type in (9) and instance.state in(7) THEN '停止' WHEN instance.state in (6) THEN '失败' WHEN instance.state in (0,1,8,10,11) THEN '执行中' WHEN instance.state in(2,3) THEN '暂停' WHEN instance.state in(4,5) THEN '停止' WHEN instance.state in(7) THEN '完成' WHEN instance.state in(9) THEN '未知' ELSE '未知' END as state_name, date_format(instance.schedule_time,'%Y-%m-%d %H:%i:%s') as schedule_date_time from t_ds_process_instance instance join t_ds_process_definition define ON instance.process_definition_code = define.code left join t_ds_user u on instance.executor_id = u.id join t_ds_project p on define.project_code = p.code where instance.is_sub_process=0 and define.project_code = #{projectCode} and LOCATE("COMPLEMENT_DATA",instance.history_cmd)>0
and instance.process_definition_code = #{processDefinitionCode}
and instance.schedule_time >= #{startTime} and instance.schedule_time <= #{endTime}
and instance.executor_id = #{executorId}
order by instance.start_time desc order by instance.start_time asc
) pro where 1=1 and pro.state_name=#{stateVal}
order by pro.schedule_date_time desc order by pro.schedule_date_time asc order by pro.end_time desc order by pro.end_time asc order by pro.run_times desc order by pro.run_times asc
